]

// Ïû¨ÏÇ¨Ïö© Í∞ÄÎä•Ìïú Ïπ¥Îìú Ïª¥Ìè¨ÎÑåÌä∏ Î∂ÑÎ¶¨
function LocationCard({ item, isExplore, onDelete, onFavorite, onAdd }) {
  return (
    <div className="grid__card">
      <div className="card__img-wrapper">
        <img src={item.img} alt={item.titleEng || item.name} />
        {isExplore ? (
          <>
          <span className="badge">POPULAR</span>
          <button className="card__add" onClick={onAdd} title="Ï∂îÍ∞ÄÌïòÍ∏∞">+</button>
          </>
        ) : (
          <>
          <span className={`card__favorite${item.favorite ? ' --active' : ''}`} onClick={onFavorite}>{item.favorite ? '‚ô•' : '‚ô°'}</span>
          <button className="card__delete" onClick={onDelete} title="ÏÇ≠Ï†úÌïòÍ∏∞">√ó</button>
          </>
        )}
      </div>
      <div className="card__body">
        <h3 className="card__title">{item.name || item.titleEng}
          <span className="card__loc">/{item.location}</span>
        </h3>
