        <p className="card__addr">{item.address}</p>
        {item.description && (
          <p className="card__description">{item.description}</p>
        )}
        <div className="card__meta">
          <span>üè∑Ô∏è Type: {item.type}</span>

          <span>‚è±Ô∏è Stay Time: {item.stay}</span>
