}

export default function Saved() {
  const [activeTab, setActiveTab] = useState('Explore');
  const [search, setSearch] = useState('');
  const [savedLocations, setSavedLocations] = useState([]);
  const [exploreLocations, setExploreLocations] = useState([]);

  // localStorageÏóêÏÑú Ï†ÄÏû•Îêú ÏúÑÏπò Ï†ïÎ≥¥Î•º Î°úÎìúÌïòÎäî Ìï®Ïàò
  useEffect(() => {
    const loadSavedLocations = () => {
      try {
        const markers = JSON.parse(localStorage.getItem('markers')) || [];
        // Îç∞Ïù¥ÌÑ∞ ÌòïÏãù ÌôïÏù∏ Î∞è Î≥¥ÏôÑ
        const processedMarkers = markers.map(marker => ({
          ...marker,
          // Í∏∞Î≥∏ Ïù¥ÎØ∏ÏßÄ ÏóÜÎäî Í≤ΩÏö∞ Ï≤òÎ¶¨ (ÎÇ¥Ïû•Îêú Ïù¥ÎØ∏ÏßÄ ÏÇ¨Ïö©)
          img: marker.img || (marker.id % 6 === 0 ? img6 : 
                marker.id % 5 === 0 ? img5 :
                marker.id % 4 === 0 ? img4 :
                marker.id % 3 === 0 ? img3 :
                marker.id % 2 === 0 ? img2 : img1),
          // idÍ∞Ä ÏóÜÎäî Í≤ΩÏö∞ ÏÉàÎ°ú ÏÉùÏÑ±
          id: marker.id || new Date().getTime() + Math.floor(Math.random() * 1000),
          // titleKorÏù¥ ÏóÜÏúºÎ©¥ nameÏúºÎ°ú ÏÑ§Ï†ï
          titleKor: marker.titleKor || marker.name,
          // titleEngÍ∞Ä ÏóÜÏúºÎ©¥ nameÏúºÎ°ú ÏÑ§Ï†ï
          titleEng: marker.titleEng || marker.name,
          // locationÏù¥ ÏóÜÎäî Í≤ΩÏö∞ Ï£ºÏÜåÏóêÏÑú Ï∂îÏ∂ú ÏãúÎèÑ
          location: marker.location || extractLocation(marker.address) || 'Unknown',
          // Í∏∞Î≥∏ Ïú†Ìòï ÏÑ§Ï†ï
          type: marker.type || 'Landmark',
          // Í∏∞Î≥∏ Ï≤¥Î•òÏãúÍ∞Ñ ÏÑ§Ï†ï
          stay: marker.stay || '1h',
          // Í∏∞Î≥∏Ï†ÅÏúºÎ°ú Ïù∏Í∏∞ ÏïÑÎãò
          popular: marker.popular || false,
          // Í∏∞Î≥∏Ï†ÅÏúºÎ°ú favorite ÏïÑÎãò
          favorite: marker.favorite || false
        }));
        setSavedLocations(processedMarkers);
      } catch (error) {
        console.error('Ï†ÄÏû•Îêú ÏúÑÏπò Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§:', error);
        setSavedLocations([]);
      }
    };

    loadSavedLocations();
  }, []);

  // exploreListÏóêÏÑú Îç∞Ïù¥ÌÑ∞ Î°úÎìú
  useEffect(() => {
    const loadExploreLocations = () => {
      setExploreLocations(exploreList);
    };
    loadExploreLocations();
  }, []);

  // exploreÏóê ÏûàÎäî Ïû•ÏÜåÎ•º savedÏóê Ï∂îÍ∞Ä
  const saveMarkerToLocalStorage = (object) => {
    // ÌòÑÏû¨ ÏãúÍ∞ÑÏùÑ IDÎ°ú ÏÇ¨Ïö©
    const id = new Date().getTime();

    const markerObj = {
      id: id,
      name: object.name,
      description: object.description, // Ïû•ÏÜå ÏÑ§Î™Ö Ï∂îÍ∞Ä
      address: object.address,
      lat: object.lat,
      lng: object.lng,
      img: object.img, // ÏóÖÎ°úÎìúÎêú Ïù¥ÎØ∏ÏßÄ ÎòêÎäî Í∏∞Î≥∏ Ïù¥ÎØ∏ÏßÄ
      location: object.location, // Ï∂îÏ∂úÌïú ÏúÑÏπò (ÏÑúÏö∏, Î∂ÄÏÇ∞ Îì±)
      type: object.type, // Ïû•ÏÜå Ïú†Ìòï
      stay: object.stay, // Ï≤¥Î•ò ÏãúÍ∞Ñ
      popular: object.popular, // Í∏∞Î≥∏Í∞íÏúºÎ°ú Ïù∏Í∏∞ Ïû•ÏÜå
      savedDate: new Date().toISOString(), // Ï†ÄÏû• ÎÇ†Ïßú
      favorite: false,
    };

    // localStorageÏóê Í∏∞Ï°¥ Îç∞Ïù¥ÌÑ∞ Î∂àÎü¨Ïò§Í∏∞
    let markers = JSON.parse(localStorage.getItem("markers")) || [];
    markers.push(markerObj);

    //stateÏóê ÏÉàÎ°úÏö¥ Îç∞Ïù¥ÌÑ∞ Ï†ÄÏû•
    setSavedLocations(markers);

    // localStorageÏóê Ï†ÄÏû•
    localStorage.setItem("markers", JSON.stringify(markers));

    // Ï†ÄÏû• ÏôÑÎ£å ÏïåÎ¶º Î∞è ÏÉÅÌÉú Ï¥àÍ∏∞Ìôî
    alert('ÏúÑÏπòÍ∞Ä Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§! Saved ÌÉ≠ÏóêÏÑú ÌôïÏù∏ÌïòÏÑ∏Ïöî.');
  };


  // Ï£ºÏÜåÏóêÏÑú ÏßÄÏó≠Î™Ö Ï∂îÏ∂ú Ìï®Ïàò
  const extractLocation = (address) => {
    if (!address) return '';

    // Ïãú/ÎèÑ Ï∂îÏ∂ú (ÏÑúÏö∏ÌäπÎ≥ÑÏãú, Í≤ΩÍ∏∞ÎèÑ Îì±)
    const cityMatch = address.match(/([^\s]+Ïãú|[^\s]+ÎèÑ|[^\s]+Íµ∞)/);
    if (cityMatch) {
      // "ÌäπÎ≥ÑÏãú", "Í¥ëÏó≠Ïãú" Îì±Ïùò Ï†ëÎØ∏ÏÇ¨ Ï†úÍ±∞
      return cityMatch[0].replace(/ÌäπÎ≥ÑÏãú|Í¥ëÏó≠Ïãú|ÏûêÏπòÏãú/, '').trim();
    }
    return '';
  };

  // Ï†ÄÏû•Îêú ÏúÑÏπò ÏÇ≠Ï†ú Ìï®Ïàò
  const deleteLocation = (index) => {
    const updatedLocations = [...savedLocations];
    updatedLocations.splice(index, 1);
    setSavedLocations(updatedLocations);
    localStorage.setItem('markers', JSON.stringify(updatedLocations));
  };
  // favorite ÏÉÅÌÉú ÌÜ†Í∏Ä Ìï®Ïàò
  const handleFavorite = (index) => {
    console.log("ÏÑ±Í≥µ");
    const updatedFavorite = [...savedLocations]
    updatedFavorite[index].favorite = !updatedFavorite[index].favorite; 
    setSavedLocations(updatedFavorite);
    localStorage.setItem('markers', JSON.stringify(updatedFavorite));
  };


  // Plan, Save Ï†ÑÏ≤¥Î•º Ìïú Î≤àÏóê Í≤ÄÏÉâ (ÌÉ≠ Íµ¨Î∂Ñ ÏóÜÏù¥ Î™®Îì† Ïπ¥Îìú+Ï†ÄÏû• Ïû•ÏÜå ÌÜµÌï©)
  const allData = [
    //...cardData,
    ...savedLocations
  ];
  const lowerSearch = search.trim().toLowerCase();
  const filtered = allData.filter(item => {
    const matchSearch =
      item.titleEng?.toLowerCase().includes(lowerSearch) ||
      item.titleKor?.toLowerCase().includes(lowerSearch) ||
      item.name?.toLowerCase().includes(lowerSearch);
    return lowerSearch === '' || matchSearch;
  });

  //explore Í≤ÄÏÉâ
  const exploreData = [
    ...exploreLocations
  ];
  const filtered_explore = exploreData.filter(item => {
    const matchSearch =
      item.titleEng?.toLowerCase().includes(lowerSearch) ||
      item.titleKor?.toLowerCase().includes(lowerSearch) ||
      item.name?.toLowerCase().includes(lowerSearch);
    return lowerSearch === '' || matchSearch;
  });

  return (
    <main className="saved">
      {/* Hero */}
      <section className="saved__hero">
        <h1>‚ÄúYour next adventure starts here‚Äù</h1>
        <p>‚ÄúFind places you‚Äôll love, just around the corner.‚Äù</p>
      </section>

      {/* Tabs & Search */}
      <div className="saved__controls">
        <div className="tabs">
          {tabList.map(tab => (
            <button
              key={tab}
              className={`tab ${activeTab === tab ? 'tab--active' : ''}`}
              onClick={() => setActiveTab(tab)}
            >
              {tab}
            </button>
          ))}
        </div>
        <div className="search">
          <input
            type="text"
            placeholder="Search..."
            value={search}
            onChange={e => setSearch(e.target.value)}
          />
        </div>
      </div>

      {/* Card Grid */}
      <div className="grid">
        {activeTab === 'Explore' && (
          // Explore ÌÉ≠ÏóêÏÑúÎäî ÏÉàÎ°úÏö¥ Ïû•ÏÜå Ï∂îÏ≤ú, +Î≤ÑÌäºÏúºÎ°ú saveÌïòÍ∏∞
          filtered_explore.map((item,index) => (
            <LocationCard
              key={item.id}
              item={item}
              isExplore={true}
              onAdd={() => saveMarkerToLocalStorage(item)}
              onDelete={() => deleteLocation(index)}
              onFavorite={()=> handleFavorite(index)}
            />
          ))
        )}
        {activeTab === 'Favorite' && (
          // Favorite ÌÉ≠ÏóêÏÑúÎäî favorite ÏÉÅÌÉúÏù∏ Ïû•ÏÜåÎßå ÌëúÏãú, ÌïòÌä∏Î•º ÎàåÎü¨ favorite ÏÉÅÌÉú Ï∑®ÏÜå
          filtered.map((item,index) => (
            item.favorite && (
            <LocationCard
              key={item.id}
              item={item}
              isExplore={false}
              onDelete={() => deleteLocation(index)}
              onFavorite={()=> handleFavorite(index)}
            />)
          ))
        )}
        {activeTab === 'Saved' && (
          // Save ÌÉ≠ÏóêÏÑúÎäî saveÎêú Ïû•ÏÜåÎ•º ÌëúÏãú, ÌïòÌä∏Î•º ÎàåÎü¨ favorite ÏÉÅÌÉúÎ°ú Î≥ÄÍ≤Ω, xÎ•º ÎàåÎü¨ ÏÇ≠Ï†ú
          savedLocations.length > 0 ? (
            filtered.map((item, index) => (
              <LocationCard
                key={item.id || index}
                item={item}
                isExplore={false}
                onDelete={() => deleteLocation(index)}
                onFavorite={()=> handleFavorite(index)}
              />
            ))
          ) : (
            <div className="no-saved-locations">
              <p>Ï†ÄÏû•Îêú ÏúÑÏπòÍ∞Ä ÏóÜÏäµÎãàÎã§.</p>
              <p>ÏßÄÎèÑÏóêÏÑú ÏúÑÏπòÎ•º Ï†ÄÏû•Ìï¥Î≥¥ÏÑ∏Ïöî!</p>
            </div>
          )
        )}
      </div>
    </main>
  );
}
