import React from 'react';
import './Modal_edit.css';

export default function PlanModalEdit({ visible, onClose, onEdit, planList, setPlanList, savedLocations }) {
  if (!visible) return null;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal" onClick={e => e.stopPropagation()}>
        {/* Header */}
        <div className="modal-header">
          <h2>Edit plan</h2>
          <button className="modal-close" onClick={onClose}>⨉</button>
        </div>

        {/* Body */}
        <div className="modal-body">
          <label className="modal-label">Plan List</label>
          <div className="modal-input-group">
            {/* planList에 있는 각 장소를 카드 형식으로 렌더링 */}
            {planList.map((place) => (
              <div key={place.id} className="plan_card">
                <img
                  src={place.img || place.image || ''}
                  alt={place.name || place.title}
                  className="plan_card__image"
                />
                <div className="plan_card__body">
                  <div className="plan_card__title">
                    <span>{place.name || place.title}</span>
                  </div>
                  {place.description && (
                    <p className="plan_card__desc">{place.description}</p>
                  )}
                  {place.address && (
                    <div className="plan_card__address">
                      <span>{place.address}</span>
                    </div>
                  )}
                </div>
                <button
                  className="plan_card__delete"
                  title="삭제하기"
                  onClick={() => {
                    // TODO: 장소 삭제 로직 구현
                    // setPlanList(prev => prev.filter(p => p.id !== place.id));
                  }}
                >
                  ×
                </button>
              </div>
            ))}

            {/* 장소 추가 버튼 */}
            <button
              className="plan-page__add-button"
              onClick={() => {
                // TODO: 장소 추가 로직 구현
              }}
            >
              + 장소 추가
            </button>
          </div>
        </div>

        {/* Footer */}
        <div className="modal-footer">
          <button className="btn-create" onClick={onEdit}>
            수정
          </button>
        </div>
      </div>
    </div>
  );
}
