import React from 'react';

const cardData = [
  { icon: '‚òÅÔ∏è', title: 'Address', text: 'Sejong University' },
  { icon: 'üë§', title: 'Person',  text: 'Hong Gil Dong' },
  { icon: '‚è∞', title: 'Plan',    text: 'Lunch, Study' },
];

export default function Home() {
  // Ïù∏ÎùºÏù∏ Ïä§ÌÉÄÏùº Ï†ïÏùò
  const heroStyle = {
    backgroundColor: '#3366ff',
    color: '#fff',
    textAlign: 'center',
    padding: '6rem 1rem',
  };
  const heroTitle = {
    fontSize: '2.5rem',
    marginBottom: '1rem',
    fontWeight: 'bold',
    letterSpacing: '1px',
  };
  const heroSubtitle = {
    fontSize: '1.1rem',
    marginBottom: '2rem',
  };
  const heroButton = {
    backgroundColor: '#fff',
    color: '#3366ff',
    border: 'none',
    padding: '0.75rem 1.5rem',
    fontSize: '1rem',
    borderRadius: '4px',
    cursor: 'pointer',
  };

  const infoSection = {
    padding: '4rem 2rem',
    backgroundColor: '#fafafa',
    textAlign: 'center',
  };
  const infoTitle = {
    fontSize: '1.75rem',
    marginBottom: '2rem',
  };
  const infoBody = {
    display: 'flex',
    flexWrap: 'wrap',
    gap: '2rem',
    maxWidth: '1200px',
    margin: '0 auto',
  };
  const mapContainer = {
    flex: 2,
    height: '400px',
    borderRadius: '8px',
    overflow: 'hidden',
    boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
  };
  const cardsContainer = {
    flex: 1,
    display: 'flex',
    flexDirection: 'column',
    gap: '1.5rem',
  };
  const cardStyle = {
    backgroundColor: '#fff',
    padding: '1.5rem',
    borderRadius: '8px',
    boxShadow: '0 2px 8px rgba(0,0,0,0.05)',
    textAlign: 'left',
  };
  const cardIcon = {
    fontSize: '2rem',
    marginBottom: '0.5rem',
  };
  const cardTitle = {
    marginBottom: '0.5rem',
    fontSize: '1.1rem',
  };
  const cardText = {
    color: '#666',
    fontSize: '0.95rem',
  };

  return (
    <>
      {/* Hero Section */}
      <section style={heroStyle}>
        <h1 style={heroTitle}>INTERACTIVE TRAVEL PLANNER</h1>
        <p style={heroSubtitle}>‚ÄúPlan your next adventure with ease and convenience.‚Äù</p>
        <button style={heroButton}>Get Started</button>
      </section>

      {/* Info + Map + Cards Section */}
      <section style={infoSection}>
        <h2 style={infoTitle}>‚ÄúPlan Your Perfect Getaway With Easy-To-Use Tools.‚Äù</h2>
        <div style={infoBody}>
          <div style={mapContainer}>
            {/* Ïã§Ï†ú Kakao Map SDK ÏÇ¨Ïö© Ïãú Ïó¨Í∏∞Ïóê kakao.maps.Map Ï¥àÍ∏∞Ìôî ÏΩîÎìúÎ•º ÎÑ£ÏúºÏÑ∏Ïöî */}
            <iframe
              title="map"
              src="https://map.kakao.com/?q=ÏÑ∏Ï¢ÖÎåÄÌïôÍµê"
              width="100%"
              height="100%"
              style={{ border: 0 }}
            />
          </div>

          <div style={cardsContainer}>
            {cardData.map(({ icon, title, text }) => (
              <div key={title} style={cardStyle}>
                <div style={cardIcon}>{icon}</div>
                <h3 style={cardTitle}>{title}</h3>
                <p style={cardText}>{text}</p>
              </div>
            ))}
          </div>
        </div>
      </section>
    </>
  );
}
