          // titleEngÍ∞Ä ÏóÜÏúºÎ©¥ nameÏúºÎ°ú ÏÑ§Ï†ï
          titleEng: marker.titleEng || marker.name,
          // locationÏù¥ ÏóÜÎäî Í≤ΩÏö∞ Ï£ºÏÜåÏóêÏÑú Ï∂îÏ∂ú ÏãúÎèÑ
          location: marker.location || extractLocation(marker.address) || 'Unknown',
          // Í∏∞Î≥∏ Ïú†Ìòï ÏÑ§Ï†ï
          type: marker.type || 'Landmark',
          // Í∏∞Î≥∏ Ï≤¥Î•òÏãúÍ∞Ñ ÏÑ§Ï†ï
          stay: marker.stay || '1h',
          // Í∏∞Î≥∏Ï†ÅÏúºÎ°ú Ïù∏Í∏∞ ÏïÑÎãò
          popular: marker.popular || false
        }));
        setSavedLocations(processedMarkers);
      } catch (error) {
        console.error('Ï†ÄÏû•Îêú ÏúÑÏπò Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§:', error);
        setSavedLocations([]);
      }
    };

    loadSavedLocations();
  }, []);

  // Ï£ºÏÜåÏóêÏÑú ÏßÄÏó≠Î™Ö Ï∂îÏ∂ú Ìï®Ïàò
  const extractLocation = (address) => {
    if (!address) return '';

    // Ïãú/ÎèÑ Ï∂îÏ∂ú (ÏÑúÏö∏ÌäπÎ≥ÑÏãú, Í≤ΩÍ∏∞ÎèÑ Îì±)
    const cityMatch = address.match(/([^\s]+Ïãú|[^\s]+ÎèÑ|[^\s]+Íµ∞)/);
    if (cityMatch) {
      // "ÌäπÎ≥ÑÏãú", "Í¥ëÏó≠Ïãú" Îì±Ïùò Ï†ëÎØ∏ÏÇ¨ Ï†úÍ±∞
      return cityMatch[0].replace(/ÌäπÎ≥ÑÏãú|Í¥ëÏó≠Ïãú|ÏûêÏπòÏãú/, '').trim();
    }
    return '';
  };

  // Ï†ÄÏû•Îêú ÏúÑÏπò ÏÇ≠Ï†ú Ìï®Ïàò
  const deleteLocation = (index) => {
    const updatedLocations = [...savedLocations];
    updatedLocations.splice(index, 1);
    setSavedLocations(updatedLocations);
    localStorage.setItem('markers', JSON.stringify(updatedLocations));
  };

  // Explore, Plan, Save Ï†ÑÏ≤¥Î•º Ìïú Î≤àÏóê Í≤ÄÏÉâ (ÌÉ≠ Íµ¨Î∂Ñ ÏóÜÏù¥ Î™®Îì† Ïπ¥Îìú+Ï†ÄÏû• Ïû•ÏÜå ÌÜµÌï©)
  const allData = [
    ...cardData,
    ...savedLocations
  ];
  const lowerSearch = search.trim().toLowerCase();
  const filtered = allData.filter(item => {
    const matchSearch =
      item.titleEng?.toLowerCase().includes(lowerSearch) ||
      item.titleKor?.toLowerCase().includes(lowerSearch) ||
      item.name?.toLowerCase().includes(lowerSearch);
    return lowerSearch === '' || matchSearch;
  });

  return (
    <main className="saved">
      {/* Hero */}
      <section className="saved__hero">
        <h1>‚ÄúYour next adventure starts here‚Äù</h1>
        <p>‚ÄúFind places you‚Äôll love, just around the corner.‚Äù</p>
      </section>

      {/* Tabs & Search */}
      <div className="saved__controls">
        <div className="tabs">
          {tabList.map(tab => (
            <button
              key={tab}
              className={`tab ${activeTab === tab ? 'tab--active' : ''}`}
              onClick={() => setActiveTab(tab)}
            >
              {tab}
            </button>
          ))}
        </div>
        <div className="search">
          <input
            type="text"
            placeholder="Search..."
            value={search}
            onChange={e => setSearch(e.target.value)}
          />
        </div>
      </div>

      {/* Card Grid */}
      <div className="grid">
        {activeTab !== 'Save' ? (
          // Explore ÎòêÎäî Plan ÌÉ≠ÏóêÏÑúÎäî Í∏∞Ï°¥ Ïû•ÏÜå Ïπ¥Îìú ÌëúÏãú
          filtered.map(item => (
            <div key={item.id} className="grid__card">
              <div className="card__img-wrapper">
                <img src={item.img} alt={item.titleEng} />
                {item.popular && <span className="badge">POPULAR</span>}
                <span className="heart">‚ô°</span>
              </div>
              <div className="card__body">
                <h3 className="card__title">{item.titleEng}
                  <span className="card__loc">/{item.location}</span>
                </h3>
                <h4 className="card__kor">{item.titleKor}</h4>
                <p className="card__addr">{item.address}</p>
                <div className="card__meta">
                  <span>üè∑Ô∏è Type: {item.type}</span>
                  <span>‚è±Ô∏è Stay Time: {item.stay}</span>
                </div>
              </div>
            </div>
          ))
        ) : (
          // Save ÌÉ≠ÏóêÏÑúÎäî localStorageÏóê Ï†ÄÏû•Îêú ÏúÑÏπò Ï†ïÎ≥¥Î•º explore ÌòïÏãùÏúºÎ°ú ÌëúÏãú
          savedLocations.length > 0 ? (
            savedLocations.map((item, index) => (
              <div key={item.id || index} className="grid__card">
                <div className="card__img-wrapper">
                  <img src={item.img} alt={item.titleEng || item.name} />
                  {item.popular && <span className="badge">POPULAR</span>}
                  <button 
                    className="delete-btn" 
                    onClick={() => deleteLocation(index)}
                    title="ÏÇ≠Ï†úÌïòÍ∏∞"
                  >
                    √ó
                  </button>
                </div>
                <div className="card__body">
                  {/* <h3 className="card__title">{item.name}
                    <span className="card__loc">/{item.location}</span>
                  </h3> */}
                  <p className="card__addr">{item.address}</p>
                  {item.description && (
                    <p className="card__description">{item.description}</p>
                  )}
                  <div className="card__meta">
                    <span>üè∑Ô∏è Type: {item.type}</span>
                    <span>‚è±Ô∏è Stay Time: {item.stay}</span>
                  </div>
                  <div className="card__coords">
                    <span>üß≠ ÏúÑÎèÑ: {item.lat.toFixed(6)}</span>
                    <span>üß≠ Í≤ΩÎèÑ: {item.lng.toFixed(6)}</span>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <div className="no-saved-locations">
              <p>Ï†ÄÏû•Îêú ÏúÑÏπòÍ∞Ä ÏóÜÏäµÎãàÎã§.</p>
              <p>ÏßÄÎèÑÏóêÏÑú ÏúÑÏπòÎ•º Ï†ÄÏû•Ìï¥Î≥¥ÏÑ∏Ïöî!</p>
            </div>
          )
        )}
      </div>

      {/* Browse More Button */}
      <div className="saved__more">
        <button>Browse more properties</button>
      </div>
    </main>
  );
}
