import React, { useState, useEffect, useRef } from 'react';
import './Plan.css';
import { infoWindowRef, showRouteBetweenPoints, openInfoWindow } from './Map.js';
import PlanModal from '../components/Modal.js';
import PlanModalEdit from '../components/Modal_edit.js';

import img1 from '../assets/1.PNG';
import img2 from '../assets/2.PNG';
import img3 from '../assets/3.PNG';
import img4 from '../assets/4.PNG';
import img5 from '../assets/5.PNG';
import img6 from '../assets/6.PNG';
import imgDefault from '../assets/dafault-place.png';


const tabList = ['Plan', 'Place'];


export default function Plan() {
  // ÌòÑÏû¨ ÏÑ†ÌÉùÎêú ÌÉ≠ ÏÉÅÌÉú (Plan / Favorite / Saved)
  const [activeTab, setActiveTab] = useState('Plan');
  // Í≤ÄÏÉâ Î¨∏ÏûêÏó¥
  const [search, setSearch] = useState('');
  // Î°úÏª¨ Ïä§ÌÜ†Î¶¨ÏßÄÏóêÏÑú Î∂àÎü¨Ïò® Ï†ÄÏû•Îêú Ïû•ÏÜåÎì§
  const [savedLocations, setSavedLocations] = useState([]);
  // Î°úÏª¨ Ïä§ÌÜ†Î¶¨ÏßÄÏóêÏÑú Î∂àÎü¨Ïò® Ï†ÄÏû•Îêú Í≥ÑÌöçÎì§
  const [savedPlans, setSavedPlans] = useState([]);

  // Í≥ÑÌöç Ï∂îÍ∞Ä Î™®Îã¨ Ï∞Ω Ïó¥Î¶º Ïó¨Î∂Ä
  const [isPlanModalOpen, setIsPlanModalOpen] = useState(false);
  // Í≥ÑÌöç ÏàòÏ†ï(Í≥ÑÌöç ÏïàÏóê Ïû•ÏÜåÎì§ ÏàòÏ†ï) Î™®Îã¨ Ï∞Ω Ïó¥Î¶º Ïó¨Î∂Ä
  const [isPlanModalEditOpen, setIsPlanModalEditOpen] = useState(false);

  // Í≥ÑÌöç Ï∂îÍ∞Ä Ïãú ÏÇ¨Ïö©Îê† state Î≥ÄÏàò
  const [planName, setPlanName] = useState('');
  const [planDescription, setPlanDescription] = useState('');
  const [planUploadedImage, setPlanUploadedImage] = useState(null); // ÏóÖÎ°úÎìúÎêú Ïù¥ÎØ∏ÏßÄ
  const [planImagePreview, setPlanImagePreview] = useState(''); // Ïù¥ÎØ∏ÏßÄ ÎØ∏Î¶¨Î≥¥Í∏∞ URL
  const [planEditIndex, setPlanEditIndex] = useState(0);
  // Í≥ÑÌöçÏóê Ìè¨Ìï®Îê† Ïû•ÏÜåÎì§Ïùò IDÎì§ÏùÑ Ï†ÄÏû•ÌïòÎäî state Î≥ÄÏàò
  const [placeList, setPlaceList] = useState([]);

  // naverMap ÏÇ¨Ïö© Î≥ÄÏàòÏàò
  const [mapLoaded, setMapLoaded] = useState(false);
  const markersRef = useRef([]);
  const mapRef = useRef(null);
  const currentRoutePolylinesRef = useRef([]);

  useEffect(() => {
    // localStorageÏóêÏÑú Ï†ÄÏû•Îêú Ïû•ÏÜå Ï†ïÎ≥¥Î•º Î°úÎìúÌïòÎäî Ìï®Ïàò
    const loadSavedLocations = () => {
      try {
        const markers = JSON.parse(localStorage.getItem('markers')) || [];
        // Îç∞Ïù¥ÌÑ∞ ÌòïÏãù ÌôïÏù∏ Î∞è Î≥¥ÏôÑ
        const processedMarkers = markers.map(marker => ({
          ...marker,
          // Í∏∞Î≥∏ Ïù¥ÎØ∏ÏßÄ ÏóÜÎäî Í≤ΩÏö∞ Ï≤òÎ¶¨ (ÎÇ¥Ïû•Îêú Ïù¥ÎØ∏ÏßÄ ÏÇ¨Ïö©)
          img: marker.img || (marker.id % 6 === 0 ? img6 : 
                marker.id % 5 === 0 ? img5 :
                marker.id % 4 === 0 ? img4 :
                marker.id % 3 === 0 ? img3 :
                marker.id % 2 === 0 ? img2 : img1),
          // idÍ∞Ä ÏóÜÎäî Í≤ΩÏö∞ ÏÉàÎ°ú ÏÉùÏÑ±
          id: marker.id || new Date().getTime() + Math.floor(Math.random() * 1000),
          // titleKorÏù¥ ÏóÜÏúºÎ©¥ nameÏúºÎ°ú ÏÑ§Ï†ï
          titleKor: marker.titleKor || marker.name,
          // titleEngÍ∞Ä ÏóÜÏúºÎ©¥ nameÏúºÎ°ú ÏÑ§Ï†ï
          titleEng: marker.titleEng || marker.name,
          // locationÏù¥ ÏóÜÎäî Í≤ΩÏö∞ Ï£ºÏÜåÏóêÏÑú Ï∂îÏ∂ú ÏãúÎèÑ
          location: marker.location || extractLocation(marker.address) || 'Unknown',
          // Í∏∞Î≥∏ Ïú†Ìòï ÏÑ§Ï†ï
          type: marker.type || 'Landmark',
          // Í∏∞Î≥∏ Ï≤¥Î•òÏãúÍ∞Ñ ÏÑ§Ï†ï
          stay: marker.stay || '1h',
          // Í∏∞Î≥∏Ï†ÅÏúºÎ°ú Ïù∏Í∏∞ ÏïÑÎãò
          popular: marker.popular || false,
          // Í∏∞Î≥∏Ï†ÅÏúºÎ°ú favorite ÏïÑÎãò
          favorite: marker.favorite || false
        }));
        setSavedLocations(processedMarkers);
      } catch (error) {
        console.error('Ï†ÄÏû•Îêú ÏúÑÏπò Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§:', error);
        setSavedLocations([]);
      }
    };
    // localStorageÏóêÏÑú Ï†ÄÏû•Îêú Í≥ÑÌöç Ï†ïÎ≥¥Î•º Î°úÎìúÌïòÎäî Ìï®Ïàò
    const loadSavedplans = () => {
      try {
        const plans = JSON.parse(localStorage.getItem('plans')) || [];
        setSavedPlans(plans);
      } catch (error) {
        console.error('Ï†ÄÏû•Îêú Í≥ÑÌöç Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§:', error);
        setSavedPlans([]);
      }
    };

    // naverMap Î°úÎìú ÎêòÏóàÎäîÏßÄ ÌôïÏù∏ÌïòÎäî Ìï®Ïàò
    const checkNaverMapsLoaded = () => {
      if (window.naver && window.naver.maps) {
        console.log('ÎÑ§Ïù¥Î≤Ñ ÏßÄÎèÑ APIÍ∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Î°úÎìúÎêòÏóàÏäµÎãàÎã§!');
        setMapLoaded(true);
      } else {
        console.error('ÎÑ§Ïù¥Î≤Ñ ÏßÄÎèÑ APIÎ•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§. window.naver:', window.naver);
        setTimeout(checkNaverMapsLoaded, 500);
      }
    };

    loadSavedLocations();
    loadSavedplans();
    checkNaverMapsLoaded();
  }, []);

  useEffect(() => {
      // ÏßÄÎèÑ APIÍ∞Ä Î°úÎìúÎêòÏßÄ ÏïäÏùÄ Í≤ΩÏö∞ Ï¢ÖÎ£å
      if (!mapLoaded) return;

      console.log('ÏßÄÎèÑ Ï¥àÍ∏∞Ìôî ÏãúÏûë...');

      try {
        // 1. ÏßÄÎèÑ ÏöîÏÜå Í∞ÄÏ†∏Ïò§Í∏∞
        const mapElement = document.getElementById('map');
        if (!mapElement) {
          console.error('ÏßÄÎèÑÎ•º ÌëúÏãúÌï† DOM ÏöîÏÜåÎ•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.');
          return;
        }

        // 2. Í∏∞Î≥∏ ÏúÑÏπò ÏÑ§Ï†ï (ÏÑ∏Ï¢ÖÎåÄ)
        const defaultPosition = new window.naver.maps.LatLng(37.549186395087, 127.07505567644);

        // 3. ÏßÄÎèÑ ÏòµÏÖò ÏÑ§Ï†ï
        const mapOptions = {
          center: defaultPosition,
          zoom: 17,
          mapTypeId: window.naver.maps.MapTypeId.NORMAL,
          mapTypeControl: true,
        };

        // 4. ÏßÄÎèÑ Í∞ùÏ≤¥ ÏÉùÏÑ±
        console.log('ÏßÄÎèÑ Í∞ùÏ≤¥ ÏÉùÏÑ± Ï§ë...');
        const map = new window.naver.maps.Map(mapElement, mapOptions);
        mapRef.current = map;
        console.log('ÏßÄÎèÑ Í∞ùÏ≤¥ ÏÉùÏÑ± ÏôÑÎ£å!');

        // 5. ÌòÑÏû¨ ÏúÑÏπò Í∞ÄÏ†∏Ïò§Í∏∞
        if (navigator.geolocation) {
          navigator.geolocation.getCurrentPosition(position => {
            const { latitude, longitude } = position.coords;
            const currentPosition = new window.naver.maps.LatLng(latitude, longitude);

            // ÌòÑÏû¨ ÏúÑÏπòÎ°ú ÏßÄÎèÑ Ïù¥Îèô
            map.setCenter(currentPosition);

            console.log('ÌòÑÏû¨ ÏúÑÏπòÎ°ú ÏßÄÎèÑ Ïù¥Îèô:', latitude, longitude);
          }, error => {
            console.warn('ÌòÑÏû¨ ÏúÑÏπòÎ•º Í∞ÄÏ†∏Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§:', error);
          });
        }

        console.log('ÏßÄÎèÑ Ï¥àÍ∏∞Ìôî ÏôÑÎ£å!');
      } catch (error) {
        console.error('ÏßÄÎèÑ Ï¥àÍ∏∞Ìôî Ï§ë Ïò§Î•ò Î∞úÏÉù:', error);
      }
    }, [mapLoaded]); // mapLoaded ÏÉÅÌÉúÍ∞Ä Î≥ÄÍ≤ΩÎê† ÎïåÎßå Ïã§Ìñâ

  // ÏÉàÎ°úÏö¥ Í≥ÑÌöç ÏÉùÏÑ± Ìï®Ïàò
  const handleAddPlan = () => {
    const newPlan = {
      id: Date.now(),
      title: planName === '' ? 'Îπà Ïù¥Î¶Ñ' : planName,
      description: planDescription === '' ? 'Îπà ÏÑ§Î™Ö' : planDescription,
      image: planUploadedImage === null ? imgDefault : planUploadedImage,
      savedDate: new Date().toISOString(), // Ï†ÄÏû• ÎÇ†Ïßú
      placeList: [],
    };

    // localStorageÏóê Í∏∞Ï°¥ Îç∞Ïù¥ÌÑ∞ Î∂àÎü¨Ïò§Í∏∞
    let plans = JSON.parse(localStorage.getItem("plans")) || [];
    // ÏÉàÎ°úÏö¥ Í≥ÑÌöç Ï∂îÍ∞Ä
    plans.push(newPlan);
    // localStorageÏóê Ï†ÄÏû•
    localStorage.setItem("plans", JSON.stringify(plans));

    setSavedPlans(prev => [...prev, newPlan]);

    // state Ï¥àÍ∏∞Ìôî
    setPlanName('');
    setPlanDescription('');
    setPlanUploadedImage(null);
    setPlanImagePreview('');
    setIsPlanModalOpen(false);
  };

  // Í≥ÑÌöç ÏàòÏ†ï(Í≥ÑÌöçÏóê Ìè¨Ìï®Îêú Ïû•ÏÜå ÏàòÏ†ï) Ìï®Ïàò
  const handleEditPlan = () => {
    const updatedPlans = [...savedPlans];
    updatedPlans[planEditIndex] = {
      ...updatedPlans[planEditIndex],
      placeList: placeList,
    };

    setSavedPlans(updatedPlans);
    localStorage.setItem('plans', JSON.stringify(updatedPlans));

    setPlaceList([]);
    setIsPlanModalEditOpen(false);
  };

  // Í≥ÑÌöç ÏÇ≠Ï†ú Ìï®Ïàò
  const handleDeletePlan = (index) => {
    const updatedPlans = [...savedPlans];
    updatedPlans.splice(index, 1);
    setSavedPlans(updatedPlans);
    localStorage.setItem('plans', JSON.stringify(updatedPlans));
  };

  // Ï£ºÏÜåÏóêÏÑú ÏßÄÏó≠Î™Ö Ï∂îÏ∂ú Ìï®Ïàò
  const extractLocation = (address) => {
    if (!address) return '';

    // Ïãú/ÎèÑ Ï∂îÏ∂ú (ÏÑúÏö∏ÌäπÎ≥ÑÏãú, Í≤ΩÍ∏∞ÎèÑ Îì±)
    const cityMatch = address.match(/([^\s]+Ïãú|[^\s]+ÎèÑ|[^\s]+Íµ∞)/);
    if (cityMatch) {
      // "ÌäπÎ≥ÑÏãú", "Í¥ëÏó≠Ïãú" Îì±Ïùò Ï†ëÎØ∏ÏÇ¨ Ï†úÍ±∞
      return cityMatch[0].replace(/ÌäπÎ≥ÑÏãú|Í¥ëÏó≠Ïãú|ÏûêÏπòÏãú/, '').trim();
    }
    return '';
  };

  // Í≤ÄÏÉâ ÏßÄÏõêÏùÑ ÏúÑÌïú Ï≤òÎ¶¨
  const allPlaceData = [
    ...savedLocations
  ];
  const lowerSearch = search.trim().toLowerCase();
  const filtered_place = allPlaceData.filter(item => {
    const matchSearch =
      item.titleEng?.toLowerCase().includes(lowerSearch) ||
      item.titleKor?.toLowerCase().includes(lowerSearch) ||
      item.name?.toLowerCase().includes(lowerSearch);
    return lowerSearch === '' || matchSearch;
  });

  const allPlanData = [
    ...savedPlans
  ];
  const filtered_plan = allPlanData.filter(item => {
    const matchSearch =
      item.title?.toLowerCase().includes(lowerSearch);
    return lowerSearch === '' || matchSearch;
  });

  const clearAll = () => {
    currentRoutePolylinesRef.current.forEach(polyline => polyline.setMap(null));
    currentRoutePolylinesRef.current = [];
    markersRef.current.forEach(marker => marker.setMap(null));
    markersRef.current = [];
    if (infoWindowRef.current) {
      infoWindowRef.current.setMap(null);
      infoWindowRef.current = null;
    }
  };

  // Î°úÏª¨ Ïä§ÌÜ†Î¶¨ÏßÄÏóêÏÑú idÎ°ú Ïû•ÏÜå Í∞ÄÏ†∏Ïò§Îäî Ìï®Ïàò
  const getLocationById = (id) => {
    try {
      const markers = JSON.parse(localStorage.getItem('markers')) || [];
      return markers.find(marker => marker.id === id) || null;
    } catch (error) {
      console.error('Î°úÏª¨Ïä§ÌÜ†Î¶¨ÏßÄÏóêÏÑú Ïû•ÏÜåÎ•º Ï∞æÎäî Ï§ë Ïò§Î•ò Î∞úÏÉù:', error);
      return null;
    }
  };

  // planÏùÑ ÌÅ¥Î¶≠ÌñàÏùÑ Îïå, Í≤ΩÎ°úÎ•º Î≥¥Ïó¨Ï£ºÎäî Ìï®Ïàò
  const planRoute = async (planId) => {
    const plan = savedPlans.find(p => p.id === planId);
    if (plan) {
      clearAll();
      console.log('ÌÅ¥Î¶≠Ìïú PlanÏùò Ïû•ÏÜå ID Î™©Î°ù:', plan.placeList);

      let beforePlace;

      for(let i=0;i<plan.placeList.length;i++) {
        const place = getLocationById(plan.placeList[i]);
        if(place) {
          if(i>=1) {
            const [polyline, arrowMarkers] = await showRouteBetweenPoints({
              startLat: beforePlace.lat,
              startLng: beforePlace.lng,
              endLat: place.lat,
              endLng: place.lng,
              map: mapRef
            });
            if(polyline) {
              currentRoutePolylinesRef.current.push(polyline);
              for(let j=0;j<arrowMarkers.length;j++) {
                markersRef.current.push(arrowMarkers[j]);
              }
            }
          } else {
            const position = new window.naver.maps.LatLng(place.lat, place.lng);
            const map = mapRef.current;
            map.setCenter(position);
          }
          console.log('ÏÑ†ÌÉùÎêú Ïû•ÏÜå:', place.name, place.lat, place.lng);
          beforePlace = place;
        }
      }
    } else {
      console.warn('Ìï¥Îãπ IDÏùò planÏùÑ Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§:', planId);
    }
  };

  // Ïû•ÏÜåÎ•º ÌÅ¥Î¶≠ÌñàÏùÑ Îïå, ÏßÄÎèÑÎ°ú Ïù¥Îèô, Ï†ïÎ≥¥Ï∞Ω ÎùÑÏö∞Îäî Ìï®Ïàò
  const wheresPlace = async (place) => {
    console.log(place);
    const position = new window.naver.maps.LatLng(place.lat, place.lng);
    const map = mapRef.current;
    map.setCenter(position);

    clearAll();
    const marker = new window.naver.maps.Marker({
      position: position,
      map: map
    });
    markersRef.current.push(marker);

    await openInfoWindow({
      map,
      marker,
      lat: position.lat(),
      lng: position.lng(),
      address: place.address
    });
  }

  return (
    <div className="plan-page">
      {/* 1. ÏÉÅÎã® ÌÉÄÏù¥ÌãÄ ÏòÅÏó≠ */}
      <section className="plan-page__header">
        <h1 className="plan-page__title">‚ÄúPlan your journey, live the adventure‚Äù</h1>
        <p className="plan-page__subtitle">‚ÄúHit the road. Make new memories.‚Äù</p>
      </section>

      {/* 2. ÌÉ≠ Î∞è ÌïÑÌÑ∞ ÏïÑÏù¥ÏΩò ÏòÅÏó≠ */}
      <div className="plan-page__controls">
        <div className="plan-page__tabs">
          {tabList.map((tabName) => (
            <button
              key={tabName}
              className={`tab ${activeTab === tabName ? 'tab--active' : ''}`}
              onClick={() => setActiveTab(tabName)}
            >
              {tabName}
            </button>
          ))}
        </div>
        <div className="search">
          <input
            type="text"
            placeholder="Search..."
            value={search}
            onChange={e => setSearch(e.target.value)}
          />
        </div>
      </div>

      {/* 3. Î©îÏù∏ ÏΩòÌÖêÏ∏†: Ï¢åÏ∏° Ïπ¥Îìú Î¶¨Ïä§Ìä∏ + Ïö∞Ï∏° ÏßÄÎèÑ ÏòÅÏó≠ */}
      <div className="plan-page__content">
        {/* Ï¢åÏ∏° Ïπ¥Îìú Î¶¨Ïä§Ìä∏ */}
        <div className="plan-page__list">

          {/* Plan ÌÉ≠ */}
          {activeTab === 'Plan' && filtered_plan.map((plan, index) => (
            <div key={plan.id} className="plan_card" onClick={() => planRoute(plan.id)}>
              <img
                src={plan.image}
                alt={plan.title}
                className="plan_card__image"
              />
              <div className="plan_card__body">
                <div className="plan_card__title">
                  <span>{plan.title}</span>
                </div>
                <p className="plan_card__desc">{plan.description}</p>
              </div>
              {/* favorite */}
              <button className="plan_card__delete" title="ÏÇ≠Ï†úÌïòÍ∏∞" onClick={() => handleDeletePlan(index)}>√ó</button>
              <button className="plan_card__edit" title="ÏàòÏ†ïÌïòÍ∏∞" onClick={() => {setPlanEditIndex(index); setPlaceList(plan.placeList); setIsPlanModalEditOpen(true);}}>üñâ</button>
            </div>
          ))}
          {activeTab === 'Plan' && 
            <button
              className="plan_card__add-button"
              onClick={() => setIsPlanModalOpen(true)}
            >
              + ÌîåÎûú Ï∂îÍ∞Ä
            </button>
          }

          {/* Place ÌÉ≠ */}
          {activeTab === 'Place' && filtered_place.map((place) => (
            <div key={place.id} className="plan_card" onClick={() => wheresPlace(place)}>
              <img
                src={place.img}
                alt={place.name}
                className="plan_card__image"
              />
              <div className="plan_card__body">
                <div className="plan_card__title">
                  <span>{place.name}</span>
                </div>
                <p className="plan_card__desc">{place.description}</p>
                {place.address && (
                  <div className="plan_card__address">
                    <span>{place.address}</span>
                  </div>
                )}
              </div>
              <span className={`plan_card__favorite${place.favorite ? ' --active' : ''}`} >{place.favorite ? '‚ô•' : '‚ô°'}</span>
            </div>
          ))}
        </div>

        {/* Ïö∞Ï∏° ÏßÄÎèÑ ÏòÅÏó≠ */}
        <div className="plan-page__map">
          <div id="map" className="map-placeholder">
            ÏßÄÎèÑ Ïª¥Ìè¨ÎÑåÌä∏Í∞Ä Ïó¨Í∏∞Ïóê Îì§Ïñ¥Í∞ëÎãàÎã§.
          </div>
        </div>
      </div>

      <PlanModal
        visible={isPlanModalOpen}
        onClose={() => setIsPlanModalOpen(false)}
        onCreate={handleAddPlan}
        planName={planName}
        setPlanName={setPlanName}
        description={planDescription}
        setDescription={setPlanDescription}
        uploadedImage={planUploadedImage}
        setUploadedImage={setPlanUploadedImage}
        imagePreview={planImagePreview}
        setImagePreview={setPlanImagePreview}
      />

      <PlanModalEdit
        visible={isPlanModalEditOpen}
        onClose={() => setIsPlanModalEditOpen(false)}
        onEdit={handleEditPlan}
        placeList={placeList}
        setPlaceList={setPlaceList}
        savedLocations={savedLocations}
      />
    </div>
  );
}
