import React from 'react';
import './Modal.css';

export default function PlanModal({ visible, onClose, onCreate, planName, setPlanName, description, setDescription, address, setAddress }) {
  if (!visible) return null;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal" onClick={e => e.stopPropagation()}>
        {/* Header */}
        <div className="modal-header">
          <h2>Plan Name</h2>
          <button className="modal-close" onClick={onClose}>
            ⨉
          </button>
        </div>

        {/* Body */}
        <div className="modal-body">
          <label className="modal-label">Name</label>
          <div className="modal-input-group">
            <input
              type="text"
              placeholder="이름"
              value={planName}
              onChange={e => setPlanName(e.target.value)}
            />
          </div>

          <label className="modal-label">Descript</label>
          <textarea
            rows="4"
            placeholder="설명"
            value={description}
            onChange={e => setDescription(e.target.value)}
          />

          <label className="modal-label">Address</label>
          <div className="modal-input-group">
            <input
              type="text"
              placeholder="주소"
              value={address}
              onChange={e => setAddress(e.target.value)}
            />
          </div>

          {/* 이미지 업로드 섹션 */}
          <div className="image-upload-section">
            <div className="image-preview-container" onClick={triggerFileInput}>
              {imagePreview ? (
                <img src={imagePreview} alt="장소 이미지 미리보기" className="image-preview" />
              ) : (
                <div className="upload-placeholder">
                  <span>➕</span>
                  <p>사진 추가</p>
                </div>
              )}
            </div>
            <input 
              type="file" 
              accept="image/*" 
              onChange={handleImageUpload} 
              ref={fileInputRef} 
              style={{ display: 'none' }} 
            />
          </div>
        </div>

        {/* Footer */}
        <div className="modal-footer">
          <button className="btn-create" onClick={onCreate}>
            Create
          </button>
        </div>
      </div>
    </div>
  );
}
