import React, { useEffect, useRef } from 'react';
import { initNaverMap, addMarker } from '../services/navermap';
import './Map.css';

function Map() {
  const mapRef = useRef(null);
  const mapInstanceRef = useRef(null);

  useEffect(() => {
    // 네이버 맵 스크립트 동적 로드
    const loadNaverMapScript = () => {
      const mapScript = document.createElement('script');
      mapScript.async = true;
      mapScript.src = 'https://oapi.map.naver.com/openapi/v3/maps.js?ncpKeyId=ra4y2dvddo&submodules=panorama,geocoder,drawing,visualization';
      mapScript.onload = initializeMap;
      document.head.appendChild(mapScript);
    };

    // 맵 초기화 함수
    const initializeMap = () => {
      if (mapRef.current && window.naver) {
        // 네이버 맵 초기화
        const mapInstance = initNaverMap(mapRef.current);
        mapInstanceRef.current = mapInstance;

        // 기본 마커 추가
        const defaultPosition = new window.naver.maps.LatLng(37.5665, 126.9780); // 서울시청
        const marker = addMarker(mapInstance, defaultPosition, {
          icon: {
            content: '<div class="map-marker"></div>',
            anchor: new window.naver.maps.Point(15, 15)
          }
        });

        // 마커 클릭 이벤트
        window.naver.maps.Event.addListener(marker, 'click', () => {
          const markerPosition = marker.getPosition();
          const markerObj = {
            name: '서울시청',
            address: '서울특별시 중구 세종대로 110',
            lat: markerPosition.lat(),
            lng: markerPosition.lng()
          };

          // 마커 정보 확인 및 저장 버튼 표시
          document.getElementById('markerInfo').style.display = 'block';
          document.getElementById('markerName').textContent = markerObj.name;
          document.getElementById('markerAddress').textContent = markerObj.address;

          // 저장 버튼에 클릭 이벤트 설정
          document.getElementById('saveBtn').onclick = () => saveMarkerToLocalStorage(markerObj);
        });
      }
    };

    // 네이버 맵 스크립트 로드
    if (!window.naver) {
      loadNaverMapScript();
    } else {
      initializeMap();
    }

    // 컴포넌트 언마운트 시 정리
    return () => {
      mapInstanceRef.current = null;
    };
  }, []);

  // localStorage에 마커 정보 저장
  const saveMarkerToLocalStorage = (markerObj) => {
    // localStorage에 기존 데이터 불러오기
    let markers = JSON.parse(localStorage.getItem("markers")) || [];
    markers.push(markerObj);

    // localStorage에 저장
    localStorage.setItem("markers", JSON.stringify(markers));

    // 저장 완료 알림
    alert('위치가 저장되었습니다!');
  };

  return (
    <div className="map-page">
      <div className="map-container">
        <div id="map" ref={mapRef} style={{ width: '100%', height: '100%' }}></div>
      </div>

      <div id="markerInfo" className="marker-info" style={{ display: 'none' }}>
        <h3>선택한 위치 정보</h3>
        <p><strong>이름:</strong> <span id="markerName"></span></p>
        <p><strong>주소:</strong> <span id="markerAddress"></span></p>
        <button id="saveBtn" className="save-button">이 위치 저장하기</button>
      </div>

      <div className="saved-places">
        <h3>저장된 장소</h3>
        <button id="showSaveBtn" className="show-save-button">저장 목록 보기</button>
        <div id="saveListArea" className="save-list-area"></div>
      </div>
    </div>
  );
}

export default Map;
