import React, { useEffect, useRef, useState } from 'react';
import './Map.css';

// 네이버 맵 스크립트 로드 헬퍼 함수
const loadNaverMapScript = () => {
  return new Promise((resolve) => {
    if (window.naver && window.naver.maps) {
      resolve();
      return;
    }

    const script = document.createElement('script');
    script.type = 'text/javascript';
    script.src = 'https://oapi.map.naver.com/openapi/v3/maps.js?ncpKeyId=ra4y2dvddo&submodules=panorama,geocoder,drawing,visualization';
    script.async = true;
    script.onload = () => resolve();
    document.head.appendChild(script);
  });
};

function Map() {
  const mapRef = useRef(null);
  const mapInstanceRef = useRef(null);
  const markerRef = useRef(null);
  const [markerInfo, setMarkerInfo] = useState(null);
  const [showInput, setShowInput] = useState(false);
  const [placeName, setPlaceName] = useState('');
  const [savedMarkers, setSavedMarkers] = useState([]);
  const [showSavedList, setShowSavedList] = useState(false);

  // 저장된 마커 목록 불러오기
  const loadSavedMarkers = () => {
    const markers = JSON.parse(localStorage.getItem("markers")) || [];
    setSavedMarkers(markers);
    setShowSavedList(true);
  };

  // 장소를 localStorage에 저장
  const saveMarkerToLocalStorage = () => {
    if (!markerInfo) return;

    // name이 없으면 기본값 설정
    const name = placeName.trim() || `위치 ${new Date().toLocaleTimeString()}`;

    const markerObj = {
      name: name,
      address: markerInfo.address,
      lat: markerInfo.lat,
      lng: markerInfo.lng
    };

    // localStorage에 기존 데이터 불러오기
    let markers = JSON.parse(localStorage.getItem("markers")) || [];
    markers.push(markerObj);

    // localStorage에 저장
    localStorage.setItem("markers", JSON.stringify(markers));

    // 저장 완료 알림 및 상태 초기화
    alert('위치가 저장되었습니다!');
    setPlaceName('');
    setShowInput(false);
    loadSavedMarkers();
  };

  useEffect(() => {
    // 네이버 지도 초기화 함수
    const initializeMap = async () => {
      // 네이버 맵 스크립트가 로드되었는지 확인하고 로드
      await loadNaverMapScript();

      // 스크립트 로드 후 지도 초기화
      if (!window.naver || !window.naver.maps) {
        console.error('네이버 맵 스크립트 로드 실패');
        return;
      }

      if (!mapRef.current) {
        console.error('지도 컨테이너 요소를 찾을 수 없습니다');
        return;
      }

      try {
        // 초기 위치 (서울시청)
        const latlng = new window.naver.maps.LatLng(37.5665, 126.9780);

        // 지도 옵션 설정
        const mapOptions = {
          center: latlng,
          zoom: 15,
          zoomControl: true,
          zoomControlOptions: {
            position: window.naver.maps.Position.TOP_RIGHT
          }
        };

        // 지도 인스턴스 생성
        const map = new window.naver.maps.Map(mapRef.current, mapOptions);
        mapInstanceRef.current = map;

        console.log('네이버 지도가 성공적으로 초기화되었습니다');

        // 현재 위치 가져오기
        if (navigator.geolocation) {
          navigator.geolocation.getCurrentPosition(
            (position) => {
              const currentPosition = new window.naver.maps.LatLng(
                position.coords.latitude,
                position.coords.longitude
              );
              map.setCenter(currentPosition);

              // 현재 위치에 마커 표시
              new window.naver.maps.Marker({
                position: currentPosition,
                map: map,
                icon: {
                  content: '<div class="map-current-location"></div>',
                  anchor: new window.naver.maps.Point(15, 15)
                },
                title: '현재 위치'
              });
            },
            (error) => {
              console.error('현재 위치를 가져오는데 실패했습니다:', error);
            }
          );
        }

        // 지도 클릭 이벤트 추가
        window.naver.maps.Event.addListener(map, 'click', function(e) {
          // 클릭한 위치
          const clickedPosition = e.coord;

          // 기존 마커 제거
          if (markerRef.current) {
            markerRef.current.setMap(null);
          }

          // 새 마커 생성
          const marker = new window.naver.maps.Marker({
            position: clickedPosition,
            map: map
          });
          markerRef.current = marker;

          // 주소 정보 가져오기 (Reverse Geocoding)
          window.naver.maps.Service.reverseGeocode({
            coords: clickedPosition,
            orders: [
              window.naver.maps.Service.OrderType.ADDR,
              window.naver.maps.Service.OrderType.ROAD_ADDR
            ].join(',')
          }, function(status, response) {
            if (status !== window.naver.maps.Service.Status.OK) {
              return alert('주소를 가져오는데 실패했습니다!');
            }

            // 주소 정보 추출
            const result = response.v2;
            const address = result.address.roadAddress || result.address.jibunAddress;

            // 정보창 표시
            const infoWindow = new window.naver.maps.InfoWindow({
              content: `<div class="info-window">
                        <p>${address}</p>
                        <p>위도: ${clickedPosition.lat().toFixed(6)}, 경도: ${clickedPosition.lng().toFixed(6)}</p>
                      </div>`,
              maxWidth: 300,
              backgroundColor: "#fff",
              borderColor: "#ccc",
              borderWidth: 2,
              anchorSize: new window.naver.maps.Size(10, 10),
              anchorColor: "#fff",
              pixelOffset: new window.naver.maps.Point(10, -10)
            });

            infoWindow.open(map, marker);

            // 마커 정보 상태 업데이트
            setMarkerInfo({
              lat: clickedPosition.lat(),
              lng: clickedPosition.lng(),
              address: address
            });

            // 입력 폼 표시
            setShowInput(true);
          });
        });
      } catch (error) {
        console.error('지도 초기화 중 오류 발생:', error);
      }
    };

    // 지도 초기화 함수 호출
    initializeMap();

    // 초기 저장된 마커 로드
    loadSavedMarkers();

    // 컴포넌트 언마운트 시 정리
    return () => {
      if (markerRef.current && markerRef.current.setMap) {
        markerRef.current.setMap(null);
      }
      mapInstanceRef.current = null;
    };
  }, []);

  return (
    <div className="map-page">
      <div className="map-container">
        <div ref={mapRef} style={{ width: '100%', height: '100%' }}></div>
      </div>

      {showInput && markerInfo && (
        <div className="marker-info">
          <h3>선택한 위치 정보</h3>
          <p><strong>주소:</strong> {markerInfo.address}</p>
          <p><strong>위도:</strong> {markerInfo.lat.toFixed(6)}, <strong>경도:</strong> {markerInfo.lng.toFixed(6)}</p>
          <div className="input-group">
            <label htmlFor="placeName">장소 이름:</label>
            <input 
              type="text" 
              id="placeName" 
              value={placeName} 
              onChange={(e) => setPlaceName(e.target.value)} 
              placeholder="장소 이름을 입력하세요"
            />
          </div>
          <button onClick={saveMarkerToLocalStorage} className="save-button">이 위치 저장하기</button>
        </div>
      )}

      <div className="saved-places">
        <h3>저장된 장소</h3>
        <button onClick={loadSavedMarkers} className="show-save-button">
          {showSavedList ? '목록 숨기기' : '저장 목록 보기'}
        </button>

        {showSavedList && (
          <div className="save-list-area">
            {savedMarkers.length === 0 ? (
              <p>저장된 장소가 없습니다.</p>
            ) : (
              <ul>
                {savedMarkers.map((item, index) => (
                  <li key={index} className="saved-item">
                    <strong>{item.name}</strong>
                    <p>{item.address}</p>
                    <p className="coords">위도: {item.lat.toFixed(6)}, 경도: {item.lng.toFixed(6)}</p>
                  </li>
                ))}
              </ul>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default Map;
